        } else if (decimals > decimalsTarget) {
            return value / pow10(decimals - decimalsTarget);
        } else {
            return value * pow10(decimalsTarget - decimals);
        }
    }

    function pow10(uint8 exp) internal pure returns (uint256) {
        return 10 ** uint256(exp);
    }
}

